sqlMap.select:
	 select COALESCE(f.notoc_ind,'N') as notocInd,f.notoc_ack_user as ackUser, TO_CHAR(f.notoc_ack_date,'DDMONYY HH24:MI') as ackDate ,coalesce(f.notoc_ack_ind,'N')  as ackInd,CASE F.CARR_CODE WHEN 'BR' THEN (SELECT RULE_VALUE FROM CMSRULES WHERE RULE_CODE='NOTOC_BR_REPT_ID'
LIMIT 1) ELSE '' END reptId 
	  from flt_opr_events f  where f.flt_type='D' and f.flt_key=#fltKey#  and f.sch_date=to_date(#schDate#,'DDMONYY')
